import React, { useEffect } from 'react';
import { useState } from 'react';
import axios from 'axios';

const EtatAnimaux = () => {

    const [data, setData] = useState([]);
    const [animalPrenom, setAnimalPrenom] = useState("");
    const [dateSoins, setDateSoins] = useState("");

    const loadData = async () => {
        const response = await axios.get("http://localhost:3002/animaux");
        setData(response.data);
    };

    useEffect(() => {
        loadData();
    }, []);

    console.log("animal prenom", animalPrenom)

    const filtreAnimaux = data.filter(
        (animal) => (animal.prenom === animalPrenom)
            && (animal.date_soins === dateSoins)
    );

    console.log("filtre animaux", filtreAnimaux)

    console.log('date soins', dateSoins)



    return (
        <div>
            <h1 className="centrer">Liste des animaux et de leur état</h1>
            <select
                name="animal"
                id="animal"
                value={animalPrenom}
                onChange={(e) => setAnimalPrenom(e.target.value)}
            >
                <option value="">Choisissez l'animal</option>
                {data.map((animal, index) => (
                    <option key={index} value={animal.prenom}>
                        {animal.prenom}
                        {console.log(animal.date_soins !== dateSoins)}
                    </option>

                ))}
            </select>
            <input
                type="date"
                name="date_soins"
                className="champsFormulaire"
                id="date_soins"
                style={{ width: "125px" }}
                //defaultValue={dateDuJour}
                value={dateSoins}
                onChange={(e) => setDateSoins(e.target.value)}
            />
            <label htmlFor="date_nourriture"></label>
            <div className="centrer">
                {(animalPrenom !== "") || (dateSoins !== "") ?
                    <div>
                        {filtreAnimaux.map((animal, index) => (
                            <div key={index}>
                                <div className="div_zoo_etat" style={{ width: '350px', height: '350px' }}>
                                    {/*<p style={{ textAlign: "center" }}>{animal.prenom}</p>*/}
                                    <img className="image_zoo" style={{ width: '250px', height: '250px' }}
                                        src={`http://localhost:3002/image/${animal.image}`}
                                        alt={animal.prenom}>
                                    </img>
                                    <p style={{ textAlign: "center" }}>{animal.etat}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                    :
                    <div>
                        {data.map((animal, index) => (
                            <div key={index}>
                                <div className="div_zoo_etat" style={{ width: '350px', height: '350px' }}>
                                    {/*<p style={{ textAlign: "center" }}>{animal.prenom}</p>*/}
                                    <img className="image_zoo" style={{ width: '250px', height: '250px' }}
                                        src={`http://localhost:3002/image/${animal.image}`}
                                        alt={animal.prenom}>
                                    </img>
                                    <p style={{ textAlign: "center" }}>{animal.etat}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                }
            </div>
        </div >
    );
};

export default EtatAnimaux;
