import React from 'react';
import Navigation from '../composants/Navigation';
import Footer from '../composants/Footer';
import { useState, useEffect } from 'react';
import axios from 'axios';

const Services = () => {

    const [data, setData] = useState([]);

    const loadData = async () => {
        const response = await axios.get("http://localhost:3002/services");
        setData(response.data);
    };

    useEffect(() => {
        loadData();
    }, []);

    return (
        <div>
            <Navigation />
            <h1 className="centrer">Nos services</h1>
            <div className="centrer">
                {data.map((service, index) => (
                    <div key={index}>
                        <div className="div_zoo" style={{ width: '500px', height: '300px' }}>
                            <p style={{ textTransform: 'capitalize' }}>{service.nom}</p>
                            <img style={{ width: '500px', height: '300px' }} src={`Liste-services/${service.image}`} alt={service.nom}></img>
                        </div>
                    </div>
                ))}
            </div>
            <Footer />
        </div >
    );
};

export default Services;
