import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';

const Footer = () => {

    const [data, setData] = useState([]);

    const loadData = async () => {
        const response = await axios.get('http://localhost:3002/horaires')
        setData(response.data)
    }

    useEffect(() => {
        loadData();
    }, []);

    return (
        <div className="footer">
            {data.map((horaire, index) => (
                <div key={index} className="horaires">
                    <div className="horaires_jour">{horaire.jour}</div>
                    <div className="horaires_heure">{horaire.heure}</div>
                </div>
            ))}
        </div>
    );
};

export default Footer;
