import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../composants/Footer';
import Navigation from '../composants/Navigation';
import BarreDashboardAdmin from '../composants/BarreDashboardAdmin';
import { useNavigate, useParams } from 'react-router-dom';

const DetailsModificationHoraires = () => {

    const navigate = useNavigate();

    const [data, setData] = useState([]);
    const { id } = useParams();
    const [dataHoraire, setDataHoraire] = useState([]);

    const idNombre = Number(id);

    const loadData = async () => {
        const response = await axios.get("http://localhost:3002/horaires");
        setData(response.data);
    };

    useEffect(() => {
        loadData();
    }, []);

    useEffect(() => {
        if (data.length > 0) {
            setDataHoraire(data.find(horaire => horaire.id === idNombre));
        }
    }, [data]);

    const [horaire, setHoraire] = useState({
        id: "",
        jour: "",
        heure_ouverture: "",
        heure_fermeture: "",
        ouvert_fermer: ""
    });

    useEffect(() => {
        setHoraire(dataHoraire)
    }, [dataHoraire]);

    const inputChangement = (e) => {
        const { name, value } = e.target;

        const nouvelleValeur = value;

        setHoraire({
            ...horaire,
            [name]: nouvelleValeur,
        });
    };

    const modifierHoraires = async (e) => {
        e.preventDefault();
        alert("Horaire pour le " + horaire.jour + " modifier avec succès")
        try {
            console.log('je suis dans try')
            await axios.put(`http://localhost:3002/horaires/modifier/${id}`, horaire)
        } catch (error) {
            console.log(error);
        }
    };

    const retourDashboardAdmin = () => {
        navigate("/dashboard-admin/modifier-horaires");
    }

    const relancerPage = () => {
        setTimeout(() => {
            retourDashboardAdmin();
        }, "1000");
    }

    return (
        <div>
            <div className="dashboard">
                <div>
                    <BarreDashboardAdmin />
                </div>
                <div>
                    <Navigation />
                    <div className="centrer">
                        <form className="formulaire" onSubmit={modifierHoraires}>
                            <legend>Modifier horaire</legend>
                            <div>
                                <p>Jour : {horaire.jour}</p>
                                <p>Horaire actuelle : {horaire.heure_ouverture} - {horaire.heure_fermeture}</p>
                                <input
                                    type="time"
                                    name="heure_ouverture"
                                    className="champsFormulaire"
                                    id="heure_ouverture"
                                    min="00:00"
                                    max="12:00"
                                    style={{ width: "auto" }}
                                    onChange={inputChangement}
                                />
                                <label htmlFor="heure_ouverture"></label>

                                <input
                                    type="time"
                                    name="heure_fermeture"
                                    className="champsFormulaire"
                                    id="heure_fermeture"
                                    min="00:00"
                                    max="12:00"
                                    style={{ width: "auto" }}
                                    onChange={inputChangement}
                                />
                                <label htmlFor="heure_fermeture"></label>

                                {horaire.ouvert_fermer === "Fermer" ?
                                    <>
                                        <input
                                            type="checkbox"
                                            name="ouvert_fermer"
                                            className="ouvert_fermer"
                                            id="ouvert_fermer"
                                            value="Ouvert"
                                            onClick={inputChangement}
                                        //onChange={inputChangement}
                                        />
                                        Ouvrir le zoo
                                        < label htmlFor="ouvert_fermer" ></label>
                                    </>
                                    :
                                    <>
                                        <input
                                            type="checkbox"
                                            name="ouvert_fermer"
                                            id="ouvert_fermer"
                                            value="Fermer"
                                            onClick={inputChangement}
                                        //onChange={inputChangement}
                                        />
                                        Fermer le zoo
                                        < label htmlFor="ouvert_fermer" ></label>
                                    </>
                                }
                            </div>

                            <div className="centrer">
                                <button type="submit" className="bouton_zoo">Confirmer</button>
                                <button className="bouton_zoo" onClick={retourDashboardAdmin}>Annuler</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default DetailsModificationHoraires;
