import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ModificationHoraires = () => {

    const navigate = useNavigate();

    const [data, setData] = useState([]);
    //const { id } = useParams();
    //const [dataPersonnel, setDataPersonnel] = useState([]);

    //const idNombre = Number(id);

    const loadData = async () => {
        const response = await axios.get('http://localhost:3002/horaires')
        setData(response.data)
    }

    useEffect(() => {
        loadData();
    }, []);

    const [horaire, setHoraire] = useState({
        id: "",
        jour: "",
        heure: ""
    });

    const inputChangement = (e) => {
        const { name, value } = e.target;

        const nouvelleValeur = value;

        setHoraire({
            ...horaire,
            [name]: nouvelleValeur,
        });
    };

    const modifierHoraires = async (id) => {

        try {
            await axios.put(`http://localhost:3002/horaires/modifier/${id}`, horaire)
        } catch (error) {
            console.log(error);
        }
    };

    const retourDashboardAdmin = () => {
        navigate("/dashboard-admin");
    }

    return (
        <div>
            <input
                type="time"
                name="heure"
                className="champsFormulaire"
                id="heure"
                placeholder="Nom utilisateur..."
                defaultValue={horaire.heure}
                onChange={inputChangement}
            />
            <label htmlFor="heure"></label>

            <div className="centrer">
                <button className="bouton_zoo" onClick={() => modifierHoraires(1)}>Confirmer</button>
                <button className="bouton_zoo" onClick={retourDashboardAdmin}>Annuler</button>
            </div>
        </div>
    );
};

export default ModificationHoraires;
