import React from 'react';
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import Navigation from '../composants/Navigation';
import Footer from '../composants/Footer';

const DetailsAnimaux = () => {

    const [data, setData] = useState([]);
    const [dataAnimal, setDataAnimal] = useState([]);
    const { id } = useParams();

    const idNombre = Number(id);
    console.log('id', id)

    const loadData = async () => {
        const response = await axios.get('http://localhost:3002/animaux')
        setData(response.data);
    }

    useEffect(() => {
        loadData();
    }, []);

    useEffect(() => {
        if (data.length > 0) {
            setDataAnimal(data.find(animal => animal.id === idNombre))
        }
    }, [data]);

    return (
        <div>
            <Navigation />

            <div className="centrer">
                <div className="animal" >
                    <img className="image_zoo" style={{ width: '800px', height: '500px' }}
                        /*src={`Liste-animaux/${animal.image}`} */
                        src={`http://localhost:3002/image/${dataAnimal.image}`}
                        alt={dataAnimal.prenom}>
                    </img>
                    <div className="text_zoo" style={{ textTransform: 'capitalize' }}>{dataAnimal.prenom}</div>
                </div>
            </div>

            <Footer />
        </div >
    );
};

export default DetailsAnimaux;
