import React, { useEffect, useState } from 'react';
import Navigation from '../composants/Navigation';
import Footer from '../composants/Footer';
import BarreDashboardAdmin from '../composants/BarreDashboardAdmin';
import axios from 'axios';

const AnimauxPopulaires = () => {

    const [animals, setAnimals] = useState([]);
    const [prenom, setPrenom] = useState([])

    const [nouvelAnimal, setNouvelAnimal] = useState({
        prenom: "Lion",
        nombreVues: ""
    })

    const gorille = "Gorille"

    useEffect(() => {
        axios.get('http://localhost:3002/vues-animaux')
            .then(animals => setAnimals(animals.data))
            .catch(err => console.log(err))



    }, []);

    const augmenterVue = async (e) => {
        e.preventDefault();

        console.log('je suis dans augmenterVue')
        console.log('frontend', prenom)
        console.log('type de variable prenom', typeof (prenom))


        console.log('frontend nouvel animal', nouvelAnimal)

        try {
            await axios.put(`http://localhost:3002/augmenter-vues-animal`, gorille)
        } catch (error) {
            console.log(error);
        }
    };

    const augmenterVue2 = (e) => {
        e.preventDefault();

        console.log('je suis dans augmenterVue222')
        console.log('frontend', prenom)
        console.log('type de variable prenom', typeof (prenom))

        //const prenomJson = JSON.parse(prenom)

        axios.post("http://localhost:3002/augmenter-vues-animal2", prenom)
            .then(response => {
                console.log(response.data);
            })
            .catch(error => {
                console.error(error);
            });
    };

    return (
        <div>
            <div className="dashboard">
                <div>
                    <BarreDashboardAdmin />
                </div>
                <div className="dashboard_composants_centrer">
                    <Navigation />
                    <h1 className="titre_service">Animaux populaires</h1>
                    <button onClick={augmenterVue}>augmenter vue animaux</button>
                    <button onClick={augmenterVue2}>augmenter vue animaux2</button>
                    <input
                        type="text"
                        name="prenom"
                        id="prenom"
                        //value={prenom}
                        onChange={(e) => setPrenom(e.target.value)}
                    />
                    <label htmlFor="prenom"></label>

                    <div>
                        {animals.map(animal => {
                            return (
                                <>
                                    <p>Prénom : {animal.prenom}</p>
                                    <p>Nombre de vues : {animal.nombreVues}</p>
                                </>
                            )
                        })}
                    </div>
                </div >
            </div>
            <Footer />
        </div >
    );
};

export default AnimauxPopulaires;
