import React, { useEffect, useState } from 'react';
import Navigation from '../composants/Navigation';
import Footer from '../composants/Footer';
import BarreDashboardAdmin from '../composants/BarreDashboardAdmin';
import axios from 'axios';

const AnimauxPopulaires = () => {

    const [animals, setAnimals] = useState([]);
    const [prenom, setPrenom] = useState([])

    const [nouvelAnimal, setNouvelAnimal] = useState({
        prenom: ""
    });

    useEffect(() => {
        axios.get('http://localhost:3002/vues-animaux')
            .then(animals => setAnimals(animals.data))
            .catch(err => console.log(err))
    }, []);

    console.log(animals)

    //const prenom = "Gorille"

    /*const augmenterVue = async (e) => {
        e.preventDefault();

        console.log('je suis dans augmenterVue')
        console.log('frontend', prenom)
        console.log('type de variable prenom', typeof (prenom))

        try {
            await axios.put(`http://localhost:3002/augmenter-vues-animal`, prenom)
        } catch (error) {
            console.log(error);
        }
    };*/

    const augmenterVue = (e) => {
        e.preventDefault();

        const headers = {
            "Content-Type": "multipart/form-data"
        };

        const formData = new FormData();

        formData.append("prenom", nouvelAnimal.prenom);

        console.log('je suis dans augmenterVue')
        console.log('frontend', nouvelAnimal.prenom)
        console.log('type de variable prenom', typeof (nouvelAnimal.prenom))

        try {
            axios.put(`http://localhost:3002/augmenter-vues-animal`, formData, { headers })
        } catch (error) {
            console.log(error);
        }
    };

    const inputChangement = (e) => {
        const { name, value } = e.target;

        const nouvelleValeur = value;

        setNouvelAnimal({
            ...nouvelAnimal,
            [name]: nouvelleValeur,
        });
    };

    return (
        <div>
            <div className="dashboard">
                <div>
                    <BarreDashboardAdmin />
                </div>
                <div className="dashboard_composants_centrer">
                    <Navigation />
                    <h1 className="titre_service">Animaux populaires</h1>
                    <button onClick={augmenterVue}>augmenter vue animaux</button>
                    <input
                        type="text"
                        name="prenom"
                        id="prenom"
                        value={prenom}
                        //onChange={(e) => setPrenom(e.target.value)}
                        onChange={inputChangement}
                    >

                    </input>
                    <div>
                        {animals.map(animal => {
                            return (
                                <>
                                    <p>Prénom : {animal.prenom}</p>
                                    <p>Nombre de vues : {animal.nombreVues}</p>
                                </>
                            )
                        })}
                    </div>
                </div >
            </div>
            <Footer />
        </div >
    );
};

export default AnimauxPopulaires;
