import React from 'react';
import Navigation from '../composants/Navigation';
import Footer from '../composants/Footer';
import { useState, useEffect } from 'react';
import axios from 'axios';

const Habitats = () => {

    const [data, setData] = useState([]);

    const loadData = async () => {
        const response = await axios.get("http://localhost:3002/habitats");
        setData(response.data);
    }

    useEffect(() => {
        loadData();
    }, []);

    console.log(data)

    return (
        <div>
            <Navigation />
            <h1 className="centrer">Les habitats</h1>
            <div className="centrer">
                {data.map((animal, index) => (
                    <div className="animal" key={index}>
                        <div className="decouvrez">
                            <img className="image_decouvrez" src={`Liste-animaux/${animal.image}`} alt={animal.prenom}></img>
                            <div className="text_decouvrez" style={{ textTransform: 'capitalize' }}>{animal.prenom}</div>
                        </div>
                    </div>
                ))}
            </div>
            <Footer />
        </div>
    );
};

export default Habitats;
