import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';

const ModificationHoraires = () => {
    const [data, setData] = useState([]);
    const [heureMatin, setHeureMatin] = useState("");
    const [heureSoir, setHeureSoir] = useState("");
    const [heureHoraire, setHeureHoraire] = useState("");
    const [modifierOuFermer, setModifierOuFermer] = useState("");

    const loadData = async () => {
        const response = await axios.get('http://localhost:3002/horaires')
        setData(response.data)
    }

    useEffect(() => {
        loadData();
        setHeureHoraire(heureMatin + " - " + heureSoir);
    }, [heureMatin, heureSoir]);

    console.log('heure horaire', heureHoraire)

    console.log('heure matin', heureMatin);
    console.log('heure soir', heureSoir)

    const changerHoraires = async (e) => {
        e.preventDefault();

        try {
            await axios.put(`http://localhost:3002/horaires/modifier`, heureHoraire)
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div >
            <h3>Horaire actuelle du zoo</h3>
            {data.map((horaire, index) => (
                <div key={index} className="horaires">
                    <div>{horaire.jour}</div>
                    <div>{horaire.heure}</div>
                    <select
                        name="changementHoraire"
                        id="changementHoraire"
                        //value={animalPrenom}
                        //onClick={() => setDateSoins("")}
                        onChange={(e) => setAnimalPrenom(e.target.value)}
                    >
                        <option value="">Modifier ou fermer</option>
                        <option value="Modifier">
                            Modifier
                        </option>
                        <option value="Fermer"
                            onChange={setModifierOuFermer("fermer")}>
                            Fermer
                        </option>
                    </select>
                    <input
                        type="time"
                        name="horaire"
                        className="champsFormulaire"
                        id="horaire"
                        style={{ width: "auto" }}
                        //defaultValue={dateDuJour}
                        //value={animal.date_nourriture}
                        onChange={(e) => setHeureMatin(e.target.value)}
                    />
                    <input
                        type="time"
                        name="horaire"
                        className="champsFormulaire"
                        id="horaire"
                        style={{ width: "auto" }}
                        //defaultValue={dateDuJour}
                        //value={animal.date_nourriture}
                        onChange={(e) => setHeureSoir(e.target.value)}
                    />
                    <input
                        type="checkbox"
                    />
                </div>
            ))}
            <button className="bouton_zoo" onClick={changerHoraires}>Modifier horaires</button>
        </div>
    );
};

export default ModificationHoraires;
