import React from 'react';
import { useState } from 'react';

const CreationPersonnels = () => {

    const [nouveauxPersonnels, setNouveauxPersonnels] = useState(personnelParDefaut());

    function personnelParDefaut() {
        return {
            nom_utilisateur: "personnel_zoo",
            mot_de_passe: "personnel123",
            role: "employé"
        }
    }

    /*const inputChangement = (e) => {

    }*/


    return (
        <div>
            <h1>Création compte personnels</h1>
            <form className="formulaire" onSubmit={seConnecter}>
                <legend>Connexion</legend>
                <input
                    type="text"
                    name="nom_utilisateur"
                    className="champsFormulaire"
                    id="nom_utilisateur"
                    placeholder="Nom utilisateur..."
                    value={nom_utilisateur}
                    onChange={(e) => setNom_utilisateur(e.target.value)}
                    required
                />
                <label htmlFor="nom_utilisateur"></label>

                <input
                    type="password"
                    name="mot_de_passe"
                    className="champsFormulaire"
                    id="mot_de_passe"
                    placeholder="Mot de passe..."
                    value={mot_de_passe}
                    onChange={(e) => setMot_de_passe(e.target.value)}
                    required
                />

                <label htmlFor="mot_de_passe"></label>

                <div className="centrer">
                    <button type="submit" className="bouton_zoo">Se connecter</button>
                    <button className="bouton_zoo" onClick={retourAccueil}>Annuler</button>
                </div>
            </form>

        </div>
    );
};

export default CreationPersonnels;
