import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';

const ModificationHoraires = () => {
    const [data, setData] = useState([]);
    const [heureMatin, setHeureMatin] = useState("");
    const [heureSoir, setHeureSoir] = useState("");
    const [heure, setHeure] = useState("");
    const [modifierOuFermer, setModifierOuFermer] = useState("");

    const loadData = async () => {
        const response = await axios.get('http://localhost:3002/horaires')
        setData(response.data)
    }

    useEffect(() => {
        loadData();
        //setHeure(heureMatin + " - " + heureSoir);
        setHoraire(heureMatin + " - " + heureSoir);
    }, [heureMatin, heureSoir]);

    const [horaire, setHoraire] = useState({
        id: "",
        jour: "",
        heure: ""
    });

    const inputChangement = (e) => {
        const { name, value } = e.target;

        const nouvelleValeur = value;

        setHoraire({
            ...horaire,
            [name]: nouvelleValeur,
        });
    };

    //console.log('heure horaire', heure)

    //console.log('heure soir', heureSoir)


    const changerHoraires = async (id) => {

        console.log('heure matin', typeof (heureMatin));
        console.log(id)

        const headers = {
            "Content-Type": "multipart/form-data"
        };

        const formData = new FormData();

        formData.append("heure", heureMatin);

        try {
            await axios.put(`http://localhost:3002/horaires/modifier/${id}`, formData)
        } catch (error) {
            console.log(error);
        }
    };

    //console.log('modifier ou fermer', modifierOuFermer)

    return (
        <div >
            <h3>Horaire actuelle du zoo</h3>
            {data.map((horaire, index) => (
                <div key={index} className="horaires">
                    <div>{horaire.jour}</div>
                    <div>{horaire.heure}</div>
                    <div>{horaire.id}</div>
                    <select
                        name="changementHoraire"
                        id="changementHoraire"
                        //value={horaire.id}
                        onChange={(e) => setModifierOuFermer(e.target.value)}
                    >
                        <option value="">Changer horaires</option>
                        <option value="Modifier">
                            Modifier
                        </option>
                        <option value="Fermer">
                            Fermer
                        </option>
                    </select>
                    {modifierOuFermer === "Modifier" ?
                        <div>
                            <input
                                type="text"
                                name="heure"
                                className="champsFormulaire"
                                id="heure"
                                style={{ width: "auto" }}
                                onChange={(e) => setHeureMatin(e.target.value)}
                            />
                            <label htmlFor="heure"></label>
                            {/*
                            <input
                                type="time"
                                name="horaireSoir"
                                className="champsFormulaire"
                                id="horaireSoir"
                                style={{ width: "auto" }}
                                onChange={(e) => setHeureSoir(e.target.value)}
                            />
            <label htmlFor="horaireSoir"></label>*/}
                        </div>
                        :
                        null
                    }
                    <button className="bouton_zoo" onClick={() => changerHoraires(horaire.id)}>
                        Modifier horaires
                    </button>
                </div>
            ))}
        </div >
    );
};

export default ModificationHoraires;

