import React, { useEffect, useState } from 'react';
import Navigation from '../composants/Navigation';
import Footer from '../composants/Footer';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Animaux = () => {

    const [data, setData] = useState([]);
    const [dataTrier, setDataTrier] = useState([])

    const [nouvelAnimal, setNouvelAnimal] = useState({
        prenom: "",
        nombreVues: ""
    })

    const loadData = async () => {
        const response = await axios.get("http://localhost:3002/animaux");
        setData(response.data);
    };

    useEffect(() => {
        loadData();
    }, []);

    useEffect(() => {
        if (data.length > 0) {
            setDataTrier(data.sort(function compare(a, b) {
                if (a.prenom < b.prenom) {
                    return -1;
                }
                else if (a.prenom > b.prenom) {
                    return 1;
                }
                else {
                    return 0;
                }
            }));
        }
    }, [data]);

    const inputChangement = () => {
        const { name, value } = e.target;

        const nouvelleValeur = value;

        setNouvelAnimal({
            ...nouvelAnimal,
            [name]: nouvelleValeur,
        });
    };

    const augmenterVue = async (e) => {
        e.preventDefault();

        console.log('frontend nouvel animal', nouvelAnimal)

        try {
            await axios.put(`http://localhost:3002/augmenter-vues-animal`, nouvelAnimal)
        } catch (error) {
            console.log(error);
        }
    };

    console.log(nouvelAnimal.prenom)

    return (
        <div>
            <Navigation />
            <h1 className="centrer">Nos animaux</h1>
            <div className="centrer">
                {dataTrier.map((animal, index) => (
                    <div className="animal" key={index}>
                        <div className="div_zoo" style={{ width: '250px', height: '250px' }}>
                            <Link to={`/animaux/${animal.id}/${animal.prenom}`}
                                style={{ opacity: "1" }}
                                onClick={inputChangement(animal.prenom)}

                            >
                                <img className="image_zoo" style={{ width: '250px', height: '250px' }}
                                    src={`http://localhost:3002/image/${animal.image}`}
                                    alt={animal.prenom}>
                                </img>
                            </Link>
                            <div className="text_zoo" style={{ textTransform: 'capitalize' }}>{animal.prenom}</div>
                        </div>
                    </div>
                ))}
            </div>
            <Footer />
        </div >
    );
};

export default Animaux;
