import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ModificationHoraires = () => {

    const navigate = useNavigate();

    const [data, setData] = useState([]);
    const [heureMatin, setHeureMatin] = useState("");
    const [heureSoir, setHeureSoir] = useState("");
    const [heure, setHeure] = useState("");

    const loadData = async () => {
        const response = await axios.get('http://localhost:3002/horaires')
        setData(response.data)
    }

    useEffect(() => {
        loadData();
    }, []);

    useEffect(() => {
        loadData();
        setHeure(heureMatin + " - " + heureSoir);
    }, [heureMatin, heureSoir]);

    const [horaire, setHoraire] = useState({
        id: "",
        jour: "",
        heure: heure
    });

    const inputChangement = (e) => {
        const { name, value } = e.target;

        const nouvelleValeur = value;

        setHoraire({
            ...horaire,
            [name]: nouvelleValeur,
        });
    };

    const modifierHoraires = async (id) => {

        try {
            await axios.put(`http://localhost:3002/horaires/modifier/${id}`, horaire)
        } catch (error) {
            console.log(error);
        }
    };

    const retourDashboardAdmin = () => {
        navigate("/dashboard-admin");
    }

    return (

        <div>

            <h3>Horaire actuelle du zoo</h3>
            {data.map((horaire, index) => (
                <div key={index} className="horaires">
                    <div>{horaire.jour}</div>
                    <div>{horaire.heure}</div>
                    <div>{horaire.id}</div>
                    <select
                        name="changementHoraire"
                        id="changementHoraire"
                    //value={horaire.id}
                    //onChange={(e) => setModifierOuFermer(e.target.value)}
                    >
                        <option value="">Changer horaires</option>
                        <option value="Modifier">
                            Modifier
                        </option>
                        <option value="Fermer">
                            Fermer
                        </option>
                    </select>
                    <div>
                        <input
                            type="text"
                            name="heure"
                            className="champsFormulaire"
                            id="heure"
                            style={{ width: "auto" }}
                            onChange={(e) => setHeureMatin(e.target.value)}
                        />
                        <label htmlFor="heure"></label>

                        <input
                            type="time"
                            name="horaireSoir"
                            className="champsFormulaire"
                            id="horaireSoir"
                            style={{ width: "auto" }}
                            onChange={(e) => setHeureSoir(e.target.value)}
                        />
                        <label htmlFor="horaireSoir"></label>
                    </div>
                    <button className="bouton_zoo" onClick={() => changerHoraires(horaire.id, heureMatin)}>
                        Modifier horaires
                    </button>
                </div>
            ))}
        </div >
    );
};

export default ModificationHoraires;

