import React, { useEffect, useState } from 'react';
import Navigation from '../composants/Navigation';
import Footer from '../composants/Footer';
import BarreDashboardAdmin from '../composants/BarreDashboardAdmin';
import axios from 'axios';

const AnimauxPopulaires = () => {

    const [animals, setAnimals] = useState([]);
    const [data, setData] = useState([]);
    const [dataTrier, setDataTrier] = useState([]);

    const loadData = async () => {
        const response = await axios.get("http://localhost:3002/animaux");
        setData(response.data);
    };

    useEffect(() => {
        loadData();
    }, []);


    useEffect(() => {
        axios.get('http://localhost:3002/vues-animaux')
            .then(animals => setAnimals(animals.data))
            .catch(err => console.log(err))
    }, []);

    console.log('animals', animals);
    console.log('data', data);

    useEffect(() => {
        if (data.length > 0) {
            setDataTrier(data.sort(function compare(a, b) {
                if (a.prenom < b.prenom) {
                    return -1;
                }
                else if (a.prenom > b.prenom) {
                    return 1;
                }
                else {
                    return 0;
                }
            }));
        }
    }, [data]);

    return (
        <div>
            <div className="dashboard">
                <div>
                    <BarreDashboardAdmin />
                </div>
                <div className="dashboard_composants_centrer">
                    <Navigation />
                    <h1 className="titre_service">Animaux populaires</h1>
                    {/*<div>
                        {animals.map(animal => {

                            return (
                                <>
                                    <p>Prénom : {animal.prenom}</p>
                                    <p>Nombre de vues : {animal.nombreVues}</p>
                                </>
                            )
                        })}
                    </div>*/}
                    <div className="centrer">
                        {data.map((dataAnimal, index) => (
                            <div className="animal" key={index}>
                                <div className="div_zoo" style={{ width: '250px', height: '250px' }}>
                                    <img className="image_zoo" style={{ width: '250px', height: '250px' }}
                                        src={`http://localhost:3002/image/${dataAnimal.image}`}
                                        alt={dataAnimal.prenom}
                                    >
                                    </img>
                                    {/*<div className="text_zoo" style={{ textTransform: 'capitalize' }}>{animal.prenom}</div>*/}
                                    {animals.map(animal => (
                                        <div>
                                            {
                                                dataAnimal.prenom === animal.prenom ?
                                                    <p className="titre_service">{animal.nombreVues} vues</p>
                                                    :
                                                    <></>
                                            }
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>
                </div >
            </div>
            <Footer />
        </div >
    );
};

export default AnimauxPopulaires;
