import React from 'react';
import { useState } from 'react';

const AjoutAnimaux = () => {

    const [nouvelAnimal, setNouvelAnimal] = useState('');

    const inputChangement = (e) => {
        const { name, value, type } = e.target;

        const nouvelleValeur = type === "number" ? Number(value) : value;

        setNouvelAnimal({
            ...nouvelAnimal,
            [name]: nouvelleValeur,
        });
    };

    const imageChangement = (e) => {
        if (e.target.files && e.target.files[0]) {
            const img = e.target.files[0];
            setNouvelAnimal({
                ...nouvelAnimal,
                image: img
            });
        };
    };

    const ajouterAnimaux = (e) => {
        e.preventDefault();

        const headers = {
            "Content-Type": "multipart/form-data"
        };

        const formData = new FormData();

        formData.append("prenom_animaux", nouvelAnimal.prenom);
        formData.append("race_animaux", nouvelAnimal.race);
        formData.append("habitat_animaux", nouvelAnimal.habitat);
        formData.append("description_animaux", nouvelAnimal.description);
        formData.append()
    }

    return (
        <div>
            <h1>Ajout animaux</h1>

            <form className="formulaire" /*onSubmit={creerPersonnels}*/>
                <legend>Ajout animaux</legend>
                <input
                    type="text"
                    name="prenom_animaux"
                    className="champsFormulaire"
                    id="prenom_animaux"
                    placeholder="Prénom..."
                    //onChange={inputChangement}
                    required
                />
                <label htmlFor="prenom_animaux"></label>

                <input
                    type="text"
                    name="race_animaux"
                    className="champsFormulaire"
                    id="race_animaux"
                    placeholder="Race..."
                    //onChange={inputChangement}
                    required
                />
                <label htmlFor="race_animaux"></label>

                <input
                    type="text"
                    name="habitat_animaux"
                    className="champsFormulaire"
                    id="habitat_animaux"
                    placeholder="Habitat..."
                    //onChange={inputChangement}
                    required
                />
                <label htmlFor="habitat_animaux"></label>

                <textarea
                    name="description_animaux"
                    className="champsFormulaire"
                    id="description_animaux"
                    placeholder="Description..."
                    //onChange={inputChangement}
                    required
                />
                <label htmlFor="description_animaux"></label>

                <input
                    type="file"
                    name="image_animaux"
                    className="champsFormulaire"
                    id="image_animaux"
                    //onChange={inputChangement}
                    required
                />
                <label htmlFor="image_animaux"></label>

                <div className="centrer">
                    <button type="submit" className="bouton_zoo">Créer</button>
                    <button className="bouton_zoo" /*onClick={retourAccueil}*/>Annuler</button>
                </div>
            </form>
        </div>
    );
};

export default AjoutAnimaux;
