import React from 'react';
import { useState, useRef } from 'react';
import axios from 'axios';

const AjoutAnimaux = () => {

    const [prenom, setPrenom] = useState("");

    const [nouvelAnimal, setNouvelAnimal] = useState({
        id: "",
        prenom: "",
        habitat: "",
        description: "",
        image: ""
    });

    const inputChangement = (e) => {
        const { name, value } = e.target;

        const nouvelleValeur = value;

        setNouvelAnimal({
            ...nouvelAnimal,
            [name]: nouvelleValeur,
        });
    };

    const imageChangement = (e) => {
        if (e.target.files && e.target.files[0]) {
            const img = e.target.files[0];
            setNouvelAnimal({
                ...nouvelAnimal,
                image: img
            });
        };
    };

    const ajouterAnimaux = (e) => {
        e.preventDefault();

        const headers = {
            "Content-Type": "multipart/form-data"
        };

        const formData = new FormData();

        formData.append("prenom", nouvelAnimal.prenom);
        formData.append("race", nouvelAnimal.race);
        formData.append("habitat", nouvelAnimal.habitat);
        formData.append("description", nouvelAnimal.description);
        formData.append("image", nouvelAnimal.image);

        axios.post("http://localhost:3002/ajout-animaux", formData, { headers })
            .then(response => {
                console.log(response.data);
            })
            .catch(error => {
                console.error(error);
            });

        try {
            axios.post("http://localhost:3002/ajout-animaux-vues", { prenom })
        } catch (error) {
            console.log(error);
        }
    };

    const effacer = (e) => {
        e.preventDefault();
        const confirmation = window.confirm("Etes-vous sûr de vouloir effacer votre saisie ?");
        if (confirmation) {
            nom_utilisateur.current.value = "";
            mot_de_passe.current.value = "";

        }
    }

    return (
        <div>
            <h2 className="titre_service">Ajouter animaux</h2>
            <form className="formulaire" onSubmit={ajouterAnimaux} >
                <input
                    type="text"
                    name="prenom"
                    className="champsFormulaire"
                    id="prenom"
                    placeholder="Prénom..."
                    onChange={(e) => {
                        setPrenom(e.target.value)
                        inputChangement(e)
                    }}
                    required
                />
                <label htmlFor="prenom"></label>

                <input
                    type="text"
                    name="race"
                    className="champsFormulaire"
                    id="race"
                    placeholder="Race..."
                    onChange={inputChangement}
                    required
                />
                <label htmlFor="race"></label>

                <input
                    type="text"
                    name="habitat"
                    className="champsFormulaire"
                    id="habitat"
                    placeholder="Habitat..."
                    onChange={inputChangement}
                    required
                />
                <label htmlFor="habitat"></label>

                <textarea
                    name="description"
                    className="champsFormulaire_textarea"
                    id="description"
                    placeholder="Description..."
                    onChange={inputChangement}
                    required
                />
                <label htmlFor="description"></label>

                <input
                    type="file"
                    name="image"
                    className="champsFormulaire_image"
                    id="image"
                    onChange={imageChangement}
                    required
                />
                <label htmlFor="image"></label>

                <div className="centrer">
                    <button type="submit" className="bouton_zoo">Ajouter</button>
                    <button className="bouton_zoo" onClick={effacer}>Effacer</button>
                </div>
            </form>
        </div >
    );
};

export default AjoutAnimaux;
