import React from 'react';
import Navigation from '../composants/Navigation';
import Footer from '../composants/Footer';
import { useState } from 'react';
import axios from 'axios';

const Contact = () => {

    const [nouvelleQuestion, setNouvelleQuestion] = useState({
        id: "",
        titre: "",
        description: "",
        email: ""
    });

    const inputChangement = (e) => {
        const { name, value } = e.target;
        const nouvelleValeur = value;

        setNouvelleQuestion({
            ...nouvelleQuestion,
            [name]: nouvelleValeur,
        });
    };

    const EnvoyerQuestion = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:3002//envoyer-questions', nouvelleQuestion, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            console.log('Succès:', response.data);
            console.log('newAvis', nouvelleQuestion)
            alert('Merci d\'avoir laissé votre avis, il sera traiter prochainement')
        } catch (error) {
            console.error('Erreur:', error.response ? error.response.data : error.message);
        }
    }

    return (
        <div>
            <Navigation />
            <form className="formulaire">
                <legend>Une question ? Contactez-nous</legend>
                <input
                    type="text"
                    name="titre"
                    className="champsFormulaire"
                    id="titre"
                    placeholder="Titre..."
                    required>
                </input>
                <label htmlFor="titre"></label>

                <textarea
                    name="message"
                    className="champsFormulaire"
                    id="message"
                    placeholder="Votre message..."
                    required>
                </textarea>
                <label htmlFor="titre"></label>

                <input
                    type="email"
                    name="email"
                    className="champsFormulaire"
                    id="email"
                    placeholder="Votre email..."
                    required>
                </input>
                <label htmlFor="titre"></label>

                <div className="centrer">
                    <button type="submit" value="Envoyer" className="bouton_zoo">Envoyer</button>
                    <button type="submit" value="Envoyer" className="bouton_zoo">Annuler</button>
                </div>
            </form>
            <Footer />
        </div>
    );
};

export default Contact;

